import { Component } from '@angular/core';
import { Person } from './person';

@Component({
  selector: 'app-person',
  templateUrl: './person.component.html',
  styleUrls: ['./person.component.css']
})
export class PersonComponent {
  //model: Person('DANE', 'CUPP', 76108);
  //model: Person | undefined;
  //model = new Person('Dane', 'Cupp', 76108);
  model = new Person();


  onSubmit() {
    console.log('onSubmit()');
    console.log(this.model);
  }

  isInputInvalid() {
    let isInvalid;
    isInvalid = this.model.firstName?.trim().length > 0;
    if (isInvalid || isInvalid == undefined) {
      isInvalid = this.model.lastName?.trim().length > 0;
    }
    if (isInvalid || isInvalid == undefined) {
      isInvalid = this.model.zipCode?.trim().length > 0;
    }
    return !isInvalid;
  }

  addNew() {
    

    console.log('Add New Clicked()');
    console.log(this.model);
  }

}

<div *ngIf="model">
  <div>
    <label for="firstName">First Name:</label>
    <input type="text" #firstName="ngModel" [(ngModel)]="model.firstName" placeholder="First Name" required />
    <span class="validation" [hidden]="firstName.valid || firstName.pristine">✋ First Name is required</span>
  </div>
  <div>
    <label for="lastName">Last Name:</label>
    <input type="text" #lastName="ngModel" [(ngModel)]="model.lastName" placeholder="Last Name" required />
    <span class="validation" [hidden]="lastName.valid || lastName.pristine">✋ Last Name is required</span>
  </div>
  <div>
    <label for="zip">Zip Code:</label>
    <input type="text" #zipCode="ngModel" [(ngModel)]="model.zipCode" maxlength="5" placeholder="5 digit zip code" required />
    <span class="validation" [hidden]="zipCode.valid || zipCode.pristine">✋ Zip Code is required</span>
  </div>

  <label></label>
  <button type="submit" [disabled]="isInputInvalid()" id="cmdSubmit" (click)="addNew();">Add New</button>
</div>
