import { Component } from '@angular/core';
import { Person } from './person';

@Component({
  selector: 'app-person',
  templateUrl: './person.component.html',
  styleUrls: ['./person.component.css']
})
export class PersonComponent {
  //model: Person('DANE', 'CUPP', 76108);
  //model: Person | undefined;
  //model = new Person('Dane', 'Cupp', 76108);
  model = new Person();


  onSubmit() {
    console.log('onSubmit()');
    console.log(this.model);
  }


  addNew() {
    

    console.log('Add New Clicked()');
    console.log(this.model);
  }

}

<div *ngIf="model!= undefined">
  <div>
    <label for="firstName">First Name:</label>
    <input type="text" #firstName="ngModel" [(ngModel)]="model.firstName" required />
    <span class="validation" [hidden]="firstName.valid || firstName.pristine">ðŸš¦ First Name is required</span>
  </div>
  <div>
    <label for="lastName">Last Name:</label>
    <input type="text" #lastName="ngModel" [(ngModel)]="model.lastName" required />
    <span class="validation" [hidden]="lastName.valid || lastName.pristine">ðŸš¦ Last Name is required</span>
  </div>
  <div>
    <label for="zip">Zip Code:</label>
    <input type="number" #zipCode="ngModel" [(ngModel)]="model.zipCode" required />
    <span class="validation" [hidden]="zipCode.valid || zipCode.pristine">ðŸš¦ Zip Code is required</span>
  </div>

  <label></label>
  <button type="submit" [disabled]="false" id="cmdSubmit" (click)="addNew();">Add New</button>
</div>
